import React from 'react'
import Dice from './Dice'
import { useSelector } from 'react-redux'

export default function DiceList() {

    const diceArray = useSelector(state => state.AnimalBetGameReducer.diceArray);

    const renderDiceArray = () => {
        return diceArray.map((dice, index) => {
            return <div key={index} className="col-12 text-center" style={{ marginTop: "75px" }}>
                <Dice />
            </div>
        })
    };

    return (
        <div>
            <div className="bg-white" style={{ width: 300, height: 300, borderRadius: "50%" }}>
                <div className="row">
                    {/* <div className="col-12 text-center" style={{ marginTop: "75px" }}>
                        <Dice />
                    </div>
                    <div className="col-6 text-right mt-5">
                        <Dice />
                    </div>
                    <div className="col-6 mt-5">
                        <Dice />
                    </div> */}

                    {renderDiceArray()}
                </div>
            </div>

            <div className="text-center">
                <button className="btn btn-success p2" style={{ fontSize: "25px" }}>SHAKE</button>
            </div>
        </div>
    )
}
