import React, { Fragment } from 'react'
import { useSpring, animated } from 'react-spring'

export default function Dice(props) {

  const { diceItem } = props;

  const [propsDice, set] = useSpring(() => ({
    to:{
      xyz: [360,360,360]
    },
    from:{
      xyz: [0,0,0]
    },
    config:{
      duration: 500
    },
    // reset: true
  }));

  return (
    <Fragment>
      <animated.div style={{transform: propsDice.xyz.interpolate((x,y,z) => `rotate(${x}deg) rotate(${y}deg) rotate(${z}deg)`)}} className="scene">
        <div className="cube">
          <img className="ml-3 cube__face front" style={{ width: 50 }} src={diceItem?.pic} alt={diceItem?.id} />
          <img className="ml-3 cube__face back" style={{ width: 50 }} src="./img/AnimalBetGameImage/ca.png" alt="" />
          <img className="ml-3 cube__face left" style={{ width: 50 }} src="./img/AnimalBetGameImage/bau.png" alt="" />
          <img className="ml-3 cube__face right" style={{ width: 50 }} src="./img/AnimalBetGameImage/nai.png" alt="" />
          <img className="ml-3 cube__face back" style={{ width: 50 }} src="./img/AnimalBetGameImage/tom.png" alt="" />
          <img className="ml-3 cube__face back" style={{ width: 50 }} src="./img/AnimalBetGameImage/cua.png" alt="" />
        </div>
      </animated.div>
    </Fragment>
  )
}
