import React from 'react'
import { useSelector } from 'react-redux'

export default function BetPoint(props) {
  const totalPoint = useSelector((state) => state.AnimalBetGameReducer.totalPoint);

  return (
    <div>
      <h3 className="text-center display-4">Animal Bet Game</h3>
      <div className="text-center mt-5">
        <span style={{ fontSize: '20px', borderRadius: '5%' }} className="p-3 text-white bg-danger">Reward: <span className="text-warning">{totalPoint}$</span></span>
      </div>

      <div className="text-center mt-5">
        <button style={{ fontSize: '20px', borderRadius: '5%' }} className="p-2 text-white bg-success">Play</button>
      </div>
    </div>
  )
}
