import React from 'react'
import { useDispatch } from 'react-redux';

export default function BetAnimal(props) {

    const dispatch = useDispatch();

    const {betItem} = props;

    return (
        <div className="mt-3">
            <img src={betItem?.pic} style={{ width: '100%' }} alt="" />

            <div className="bg-success mt-2 pb-2 text-center" style={{ borderRadius: '10px' }}>
                <button onClick={()=>{
                    dispatch({
                        type: 'BET',
                        betItem,
                        plusMinus: true,
                    })
                }} className="btn btn-danger mr-3">+</button>
                <span className="mt-2" style={{ color:'yellow', fontSize:25 }}>{betItem?.betPoint}</span>
                <button onClick={()=>{
                    dispatch({
                        type: 'BET',
                        betItem,
                        plusMinus: false,
                    })
                }} className="btn btn-danger ml-3">-</button>
            </div>
        </div>
    )
}
